import React from 'react';
import axios from 'axios';

import Header from '../components/Header';
import Footer from '../components/Footer';
import Brands from '../components/Brands';
import Categories from '../components/Categories';
import Subcategories from '../components/Subcategories';

function Catalog() {
    const [filters, setFilters] = React.useState(false);
    const [categories, setCategories] = React.useState([]);
    const [activeCategory, setActiveCategory] = React.useState(0);

    React.useEffect(() => {
        const getCategories = async () => {
            const res = await axios.get(`${process.env.REACT_APP_API}/categories`);
            setCategories(res.data);
        };
        getCategories();
    }, [categories]);

    const toggleFilters = () => {
        setFilters(!filters);
    };

    const setCategory = (index) => {
        setActiveCategory(index);
    };

    console.log();
    return (
        <div className="wrapper">
            <Header></Header>
            <main>
                <svg className="checkbox-symbol">
                    <symbol id="check" viewBox="0 0 12 10">
                        <polyline
                            points="1.5 6 4.5 9 10.5 1"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth="2"></polyline>
                    </symbol>
                </svg>
                <div className="page-introduction">
                    <img src="assets/img/catalog1.jpg" alt="" />
                    <img src="assets/img/catalog2.jpg" alt="" />
                </div>

                <div className="catalog">
                    <aside
                        className={
                            filters
                                ? 'catalog__sidebar catalog-sidebar open'
                                : 'catalog__sidebar catalog-sidebar'
                        }>
                        <div className="catalog-sidebar__filters filters-simple">
                            <h6>Категории</h6>
                            {categories &&
                                categories.map((item, i) => (
                                    <button
                                        key={`${item}_${i}`}
                                        onClick={() => setCategory(i)}
                                        className={activeCategory === i ? 'current' : ''}>
                                        {item.name}
                                    </button>
                                ))}
                        </div>
                        <div className="catalog-sidebar__filters filters-simple">
                            <h6>Субкатегории</h6>
                            {categories[activeCategory].subcategories &&
                                categories[activeCategory].subcategories.map((item) => (
                                    <p>{item}</p>
                                ))}
                        </div>
                        <Brands></Brands>
                        <button
                            className="icon-close close-filter"
                            onClick={toggleFilters}></button>
                    </aside>
                    <div className="catalog__container">
                        <button className="button button__dark open-filter" onClick={toggleFilters}>
                            Фильтры
                        </button>

                        <div className="catalog__body catalog-body catalog-grid">
                            <article className="catalog-body__item product">
                                <img src="img/product1.jpg" alt="" />
                                <p>Repudiandae aperiam asperiores</p>
                                <h4>1200 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product2.jpg" alt="" />
                                <p>Voluptatibus facilis voluptatibus</p>
                                <h4>16 000 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product3.jpg" alt="" />
                                <p>Officiis aut quia</p>
                                <h4>800 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product4.jpg" alt="" />
                                <p>Aliquid laborum praesentium</p>
                                <h4>4200 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product2.jpg" alt="" />
                                <p>Voluptatibus facilis voluptatibus</p>
                                <h4>16 000 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product1.jpg" alt="" />
                                <p>Repudiandae aperiam asperiores</p>
                                <h4>1200 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product3.jpg" alt="" />
                                <p>Officiis aut quia</p>
                                <h4>800 MDL</h4>
                            </article>
                            <article className="catalog-body__item product">
                                <img src="img/product4.jpg" alt="" />
                                <p>Aliquid laborum praesentium</p>
                                <h4>16 000 MDL</h4>
                            </article>
                        </div>

                        <a href="#" className="button button__dark">
                            Показать еще
                        </a>
                    </div>
                </div>
            </main>
            <Footer></Footer>
        </div>
    );
}

export default Catalog;
