import React from 'react';

function Product({ product }) {
    const getProduct = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API}/products/1`);
        setProduct(res.data);
    };

    React.useEffect(() => {
        getProduct();
    }, []);

    return (
        <article className="catalog-body__item product">
            <img src={product.images} alt="" />
            <p>{product.name}</p>
            <h4>{product.price} MDL</h4>
        </article>
    );
}

export default Product;
