import React from 'react';
import axios from 'axios';

import Product from './Product';

function Products() {
    const [products, setProducts] = React.useState([]);

    const getProducts = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API}/products`);
        setProducts(res.data);
    };

    React.useEffect(() => {
        getProducts();
    }, [products]);

    return (
        <>
            <div className="catalog__body catalog-body catalog-grid">
                {products.map((product, i) => (
                    <Product key={`${product.name}_${i}`} product={product} />
                ))}
            </div>
            {isCompleted ? (
                <button onClick={loadMore} type="button" className="button button__dark">
                    Это все
                </button>
            ) : (
                <button onClick={loadMore} type="button" className="button button__dark">
                    Показать еще
                </button>
            )}
        </>
    );
}

export default Products;

import { slice } from 'lodash';
function Posts() {
    const [post, setPost] = useState([]);
    const [isCompleted, setIsCompleted] = useState(false);
    const [index, setIndex] = useState(5);
    const initialPosts = slice(post, 0, index);
    const getData = () => {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then((res) => res.json())
            .then((json) => setPost(json))
            .catch((e) => console.log(e));
    };
    const loadMore = () => {
        setIndex(index + 5);
        console.log(index);
        if (index >= post.length) {
            setIsCompleted(true);
        } else {
            setIsCompleted(false);
        }
    };
    useEffect(() => {
        getData();
    }, []);
    return (
        <div>
            <h2 className="mb-3">React Js Load More Example</h2>
            {initialPosts.map((item) => {
                return (
                    <div className="mb-3 card bg-primary p-2 text-dark bg-opacity-25" key={item.id}>
                        <div className="card-body">{item.title}</div>
                    </div>
                );
            })}
            <div className="d-grid mt-3 mb-5"></div>
        </div>
    );
}
