import React from 'react';
import axios from 'axios';
import { slice } from 'lodash';

import Product from './Product';

function Products() {
    const [products, setProducts] = React.useState([]);
    const [isCompleted, setIsCompleted] = React.useState(false);
    const [index, setIndex] = React.useState(12);
    const initialPosts = slice(products, 0, index);

    const getProducts = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API}/products`);
        setProducts(res.data);
    };

    const loadMore = () => {
        setIndex(index + 6);
        console.log(index);
        if (index >= products.length) {
            setIsCompleted(true);
        } else {
            setIsCompleted(false);
        }
    };

    React.useEffect(() => {
        getProducts();
    }, [products]);

    return (
        <>
            <div className="catalog__body catalog-body catalog-grid">
                {initialPosts.map((product, i) => (
                    <Product key={`${product.name}_${i}`} product={product} />
                ))}
            </div>
            {isCompleted ? (
                <button onClick={loadMore} type="button" className="button button__dark">
                    Это все
                </button>
            ) : (
                <button onClick={loadMore} type="button" className="button button__dark">
                    Показать еще
                </button>
            )}
        </>
    );
}

export default Products;
