import React from 'react';
import axios from 'axios';

function Categories() {
    const [categories, setCategories] = React.useState([]);
    const [activeCategory, setActiveCategory] = React.useState(0);

    React.useEffect(() => {
        const getCategories = async () => {
            const res = await axios.get(`${process.env.REACT_APP_API}/categories`);
            setCategories(res.data);
        };
        getCategories();
    }, [categories]);

    const setCategory = (index) => {
        setActiveCategory(index);
    };

    return (
        <div className="catalog-sidebar__filters filters-simple">
            <h6>Категории</h6>
            {activeCategory}
            <button className={activeCategory == 0 ? 'current' : ''}>Все</button>
            {categories.map((cat, index) => (
                <button
                    key={`${cat}_${index}`}
                    onClick={() => setCategory(index)}
                    className={activeCategory == 0 ? 'current' : ''}>
                    {cat.name}
                </button>
            ))}
        </div>
    );
}

export default Categories;
