import React from 'react';
import axios from 'axios';

import Product from './Product';

function Products() {
    const [products, setProducts] = React.useState([]);

    const getProducts = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API}/products`);
        setProducts(res.data);
    };

    React.useEffect(() => {
        getProducts();
    }, [products]);

    return (
        <>
            <div className="catalog__body catalog-body catalog-grid">
                {products.map((product, i) => (
                    <Product key={`${product.name}_${i}`} product={product} />
                ))}
            </div>
            <button className="button button__dark">Показать еще</button>
        </>
    );
}

export default Products;

