import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Controller, Lazy, EffectCreative } from 'swiper';
import 'swiper/css';
import 'swiper/css/effect-creative';

function Slider({ product }) {
    const [swiper, updateSwiper] = React.useState(null);
    const [swiperThumbs, updateSwiperThumbs] = React.useState(null);

    let params = {
        modules: [Controller, Navigation, Lazy, EffectCreative],
        preloadImages: false,
        slidesPerView: 1,
        lazy: true,
        navigation: {
            nextEl: '.slider-product-controls__arrows .slider-arrow__next',
            prevEl: '.slider-product-controls__arrows .slider-arrow__prev',
        },
        loop: false,
        grabCursor: true,
        effect: 'creative',
        speed: 800,
        creativeEffect: {
            prev: {
                shadow: true,
                translate: [0, 0, -400],
            },
            next: {
                translate: ['100%', 0, 0],
            },
        },
    };

    let thumbsParams = {
        modules: [Controller],
        slideToClickedSlide: true,
        spaceBetween: 10,
        slidesPerView: 5,
        freeMode: true,
        watchSlidesProgress: true,
    };

    React.useEffect(() => {
        if (swiper && swiperThumbs) {
            swiper.controller.control = swiperThumbs;
            swiperThumbs.controller.control = swiper;
        }
    }, [swiper, swiperThumbs]);
    return (
        <div className="product-modal__body modal-body">
            <div className="modal-body__slider">
                <div className="product-slider__slider">
                    <Swiper {...params} className="slider-product__body">
                        <SwiperSlide className="slider-product__slide">
                            <img src={product.images} alt="" />
                        </SwiperSlide>
                        <SwiperSlide className="slider-product__slide">
                            <img src={product.images} alt="" />
                        </SwiperSlide>
                        <SwiperSlide className="slider-product__slide">
                            <img src={product.images} alt="" />
                        </SwiperSlide>
                    </Swiper>

                    <div className="slider-product-controls">
                        <div className="slider-product-controls__arrows slider-arrows">
                            <button
                                type="button"
                                className="slider-arrow slider-arrow__prev icon-chevron__left"></button>
                            <button
                                type="button"
                                className="slider-arrow slider-arrow__next icon-chevron__right"></button>
                        </div>
                    </div>
                </div>

                <div className="thumb-slider__slider">
                    <Swiper {...thumbsParams} className="slider-product__body">
                        <SwiperSlide className="slider-product__slide">
                            <img src={product.images} alt="" />
                        </SwiperSlide>
                        <SwiperSlide className="slider-product__slide">
                            <img src={product.images} alt="" />
                        </SwiperSlide>
                        <SwiperSlide className="slider-product__slide">
                            <img src={product.images} alt="" />
                        </SwiperSlide>
                    </Swiper>
                </div>
            </div>

            <div className="modal-body__content">
                <h4 className="product-name">{product.name}</h4>
                <h4 className="product-price">{product.price} MDL</h4>

                <p>{product.description}</p>
                {/* <button className="add-cart button button__dark added">
                <div className="icon-done"></div>
                Добавить в корзину
            </button> */}
            </div>
        </div>
    );
}

export default Slider;
